import classnames from 'classnames'
import type { FC } from 'react'
import { useCallback, useRef } from 'react'
import type { KLComponentType, ImageProps } from '@/types'
// import { Image } from 'antd'
import 'lazysizes'
import './index.less'

export interface ComponentsOverViewProps {
  title: string
  src: string
  alt?: string
  fallback?: string
  type: KLComponentType
  width?: number | string
  height?: number | string
  imageClassName?: string
  preview?: ImageProps['preview']
  group: '通用' | '布局' | '导航' | '数据录入' | '数据展示' | '反馈' | '其他'
}

export const ComponentsOverview: FC<ComponentsOverViewProps> = ({
  title,
  src,
  alt,
  type,
  width = 100,
  height = 50,
  imageClassName,
  preview = false,
  fallback = `data:image/png;base64,`
}) => {
  const imgRef = useRef<HTMLImageElement>(null!)

  const handleError = useCallback(() => {
    imgRef.current.src = fallback
  }, [])

  return (
    <div
      className="component-overview"
      datatype={type}
      onDragStart={e => e.dataTransfer.setData('text/plain', type)}
    >
      <div className="component-overview-img">
        <img
          data-src={src}
          alt={alt}
          ref={imgRef}
          width={width}
          height={height}
          onError={handleError}
          className={classnames('lazyload', imageClassName)}
        />
        {/* <Image
          alt={title}
          src={src}
          width={width}
          height={height}
          preview={preview}
          fallback={fallback}
          className={classnames('lazyload', imageClassName)}
        /> */}
      </div>
      <span>{title}</span>
    </div>
  )
}
