import classnames from 'classnames'
import type { FC } from 'react'
import { useCallback, useRef } from 'react'
import type { ImageProps } from '@/types'
import type { KLComponentg2Type } from '@/types/g2'
// import { Image } from 'antd'
import 'lazysizes'
import './index.less'

export interface Componentsg2OverViewProps {
  title: string
  src: string
  alt?: string
  fallback?: string
  type: KLComponentg2Type
  width?: number | string
  height?: number | string
  imageClassName?: string
  preview?: ImageProps['preview']
  group:
    | '折线图'
    | '柱状图'
    | '条形图'
    | '饼图'
    | '点图'
    | '面积图'
    | '雷达图'
    | '箱型图'
    | '烛形图'
    | '热力图'
    | '仪表盘'
    | '漏斗图'
    | '地图'
    | '分面图'
    | '关系图'
    | '其他图表'
}

export const Componentsg2Overview: FC<Componentsg2OverViewProps> = ({
  title,
  src,
  alt,
  type,
  width = 100,
  height = 50,
  imageClassName,
  preview = false,
  fallback = `data:image/png;base64,`
}) => {
  const imgRef = useRef<HTMLImageElement>(null!)

  const handleError = useCallback(() => {
    imgRef.current.src = fallback
  }, [])

  return (
    <div
      className="component-overview"
      datatype={type}
      onDragStart={e => e.dataTransfer.setData('text/plain', type)}
    >
      <div className="component-overview-img">
        <img
          data-src={src}
          alt={alt}
          ref={imgRef}
          width={width}
          height={height}
          onError={handleError}
          className={classnames('lazyload', imageClassName)}
        />
        {/* <Image
          alt={title}
          src={src}
          width={width}
          height={height}
          preview={preview}
          fallback={fallback}
          className={classnames('lazyload', imageClassName)}
        /> */}
      </div>
      <span>{title}</span>
    </div>
  )
}
